import logo from './logo.svg';
import './App.css';
import {useEffect} from 'react';

// Import web3
const Web3 = require('web3').default;
const web3 = new Web3("http://127.0.0.1:8545"); // Adjust to your provider (e.g., Ganache or Infura URL)

// Contract ABI and Address
const contractABI = require('./DecentraAid.json').abi;
const contractAddress = "0x5dB268CCfd727CFfe2e1d598b8A76F872E0EFabE";
const Contract = new web3.eth.Contract(contractABI, contractAddress);

async function getHours() {

console.log("test");

	try{
	      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      	      const account = accounts[0];


      	      const userHours = await Contract.methods.getHours().call({from: account, gas:500000, gasPrice: web3.utils.toWei('50', 'gwei')});
      	      document.getElementById("hoursOutput").innerHTML = "Hours: " + userHours;

      	      console.log(userHours);
	}catch(error){

		console.error("Error Getting Hours: ", error);
	}



}

async function getAttendance() {

console.log("test");

	try{
	      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      	      const account = accounts[0];


      	      const userAttendance = await Contract.methods.getAttendanceRate().call({from: account, gas:500000, gasPrice: web3.utils.toWei('50', 'gwei')});
      	      document.getElementById("attendanceOutput").innerHTML = "Attendance: " + userAttendance;

	}catch(error){

		console.error("Error Getting Hours: ", error);
	}



}

async function connect() {
    // Modern dapp browsers...
    console.log("Hello");
    //const account = document.getElementById("status");

      try {
        // Request account access

        console.log("Inside");
        if (window.ethereum) {
	  App.web3Provider = window.ethereum;
	  try {
	    // Request account access

	    await window.ethereum.enable();
	    //document.getElementById("status").innerHTML = "Connected";
	  } catch (error) {
	    // User denied account access...
	    console.error("User denied account access 0: ", error);
	  }

	  //account.innerHTML = 'Connected';
	}
      } catch (error) {
        // User denied account access...
        console.error("User denied account access 1: ", error);
      }

      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      const account = accounts[0];
  	document.getElementById("status").innerHTML = `Connected to account: ${account}`;

}


function App() {

useEffect(() => {

	connect();

}, []);

  return (
<div>
    <p id="status">  </p>
    <button id="getHoursButton" onClick={getHours}>Get Hours</button>
    <p id="hoursOutput"></p>

    <button id="createEventButton">Create Event</button>

    <button id="getAttendanceRate" onClick={getAttendance}>Get Attendance Rate</button>
    <p id="attendanceOutput"></p>

    <div>

        <p>Event Name: </p>
        <p>Organization Name: </p>
        <p>Location: </p>
        <p>Date: </p>
        <p>Start Time: </p>
        <p>End time: </p>
        <p>Number of Volunteers Required: </p>
        <p>Number of Leaders Required: </p>
        <p>Event Description: </p>

        <button id="eventManagementButton">Event Management</button>
      	<button id="volunteerManagementButton">Volunteer Management</button>

    </div>

    <div id="coordinatorPopup">

        <button id="startEventButton">Start Event</button>
        <button id="endEventButton">End Event</button>

        <button id="getNumberVolunteers"> Get Number of Volunteers </button>
        <p>

        </p>
        <button id="getNumberLeaders"> Get Number of Leaders </button>
        <p>

        </p>
        <button id="getVolunteerNames"> Get Number of Volunteers </button>
        <p>

        </p>

        <button id="Show"> Show </button>
        <input type="text" id="showButton"/>
        <button id="noShow">No Show</button>
        <input type="text" id="noShowButton"/>

    </div>

    <div id="volunteerPopup">
      <p>Current Number of Volunteers: </p>
      <p>Current number of Leaders: </p>
        <button id="signUp"> Signup </button>
        <input type="text" id="signUpInput"/>
        <button id="leaderSignUp"> Signup as Leader </button>
        <input type="text" id="signUpAsLeaderInput"/>
    </div>

      <div id="creatEventPopup">
        <p>Event Name: </p>
        <input type="text" id="eventNameInput"/>
        <p>Organization Name: </p>
        <input type="text" id="orgNameInput"/>
        <p>Location: </p>
        <input type="text" id="locationInput"/>
        <p>Date: </p>
        <input type="text" id="dateInput"/>
        <p>Start Time: </p>
        <input type="text" id="startTimeInput"/>
        <p>End time: </p>
        <input type="text" id="endTimeInput"/>
        <p>Number of Volunteers Required: </p>
        <input type="text" id="numberOfVolunteersInput"/>
        <p>Number of Leaders Required: </p>
        <input type="text" id="numberOfLeadersInput"/>
        <p>Event Description: </p>
        <input type="text" id="eventDescripionInput"/>

        <button id="submitEvent">Create Event</button>
    </div>


</div>
   );
}

export default App;
